mapper.select:
        SELECT * FROM elec_device_plan
        

            
                AND jctId=#{jctId}
            

            
                AND devName=#{devName}
            

            
                AND devType=#{devType}
            

            
                AND PlanDate BETWEEN #{planDatef} AND #{planDatet}
            
        

          limit #{start},#{num}

        SELECT COUNT(*) FROM elec_device_plan
        

            
                AND jctId=#{jctId}
            

            
                AND devName=#{devName}
            

            
                AND devType=#{devType}
            

            
                AND PlanDate BETWEEN #{planDatef} AND #{planDatet}
